import React from 'react'
import Manifest from './s3-thewhy.json'

const ModalSVG = () => {
    return (
        <svg className="w-7 h-7 p-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" /></svg>
    )
}

const ArrowSVG = () => {
    return (
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M17 8l4 4m0 0l-4 4m4-4H3" /></svg>
    )
}

const Section = (data) => {
    const [open, setOpen] = React.useState(false);
    var i = data.data
    return (
    <div onClick={() => setOpen(!open)} className='flex flex-col ring-1 ring-gray-200 p-4 bg-white rounded-md relative'>
        <div className='font-bold text-base leading-snug pr-5 my-3'>{i.heading}</div>
        <div className='absolute right-0 top-0 p-2'>
            <div className={ !open ? 'ml-auto text-gray-400 self-start transform rotate-0 duration-300 ease-in-out' : "ml-auto text-gray-500 self-start bg-gray-100 rounded-full transform rotate-180 duration-300 ease-in-out"}><ModalSVG /></div>
        </div>
        { 
        open ?
        <div className='space-y-4'>
            <div className='font-normal text-sm leading-snug text-gray-500'>{i.paragraph}</div>
            <div className='font-normal text-sm leading-snug text-blue-600 flex items-center space-x-2'>Invest in STAGELINK <ArrowSVG /></div>
        </div>
        : 
        null 
        }
    </div>
    )
}

const TheWhy = () => {
    return (
    <div className='flex flex-col text-black p-10 pr-5 space-y-4 realtive'>
        <div className='font-extrabold text-2xl'>{Manifest.title}</div>
        <div className='flex flex-col space-y-3'>
            {Manifest.content.map((i, key) => {
                return <Section key={key} data={i}/>;
                })}
        </div>
    </div>
    )
}

const FuckedTheWhy = () => { 
    return (
    <div className='flex flex-col text-black p-10 space-y-4'>
        <div className='font-extrabold text-2xl'>{Manifest.title}</div>
        <div className='font-normal text-base leading-snug space-y-6'>
            <div>The way we watch sports is changing and <b>STAGELINK</b> is designed to handle this.</div>
            <div>Built using the robust and secure BSC network, <b>STAGELINK</b> aims to disrupt the authoritative and bureaucratic PPV model and replace it with a decentralised and fairly priced alternative.</div>
        </div>
        <div className='font-extrabold text-xl'>Why you should invest in STAGELINK.</div>
        <div className='flex flex-col ring-1 ring-gray-800 p-10 bg-white rounded space-y-4'>
            <div className='font-bold text-xl'>We reward our investors.</div>
            <div className='font-normal text-base leading-snug'>
                <div>In mutual respect of continued involvement and interest in our goals, <b>STAGELINK</b> rewards investors with dividends in correlation to their share size.</div>
            </div>
        </div>
        <div className='flex flex-col ring-1 ring-gray-800 p-10 bg-white rounded space-y-4'>
            <div className='font-bold text-xl'>We are for the future.</div>
            <div className='font-normal text-base leading-snug'>
                <div>The emergence of influencer exhibiton fights is a new trend that is here to stay. <b>STAGELINK</b> aims to utilize the growing popularity of this type of new sport as a quickly growing form of revenue.</div>
            </div>
        </div>
    </div>
    )
}

export default TheWhy
