import { useEffect } from "react";
import { useLocation } from "react-router-dom";

export default function FadeIn() {
  const { pathname } = useLocation();

  useEffect(() => {
    let el = document.querySelector('#Fade');
    el.classList.add('fade-in');
  }, [pathname]);

  return null;
}
