import React from 'react'
import Manifest from './manifest.json'

const TextBlocks = () => {
    var text = Manifest.main
    return(
    <div className='space-y-5'>
        {text.split('/n').map((i,key) => {
            return <div key={key}>{i}</div>;
            })}
        </div>
    )
}

const SectionContent = () => {
    return (
    <div className='flex flex-col text-black p-7'>
        <div className='font-extrabold text-sm opacity-30 mb-9'>{Manifest.heading}</div>
        <div className='border-b mb-5 pb-3 border-gray-300'>
            <div className='font-extrabold text-3xl mb-3 opacity-90'>{Manifest.title}</div>
            <div className='font-medium text-base leading-snug opacity-90'><b>STAGELINK </b>{Manifest.main[0]['main']}</div>
        </div>
        <div className='space-y-4'>
            <div className='bg-white shadow rounded-xl p-5'>
                <div className='font-extrabold text-base leading-snug mb-3'>THE PROBLEM</div>
                <div className='font-medium text-base leading-snug mb-3'>{Manifest.main[1]['main']}</div>
            </div>
            <div className='bg-gray-100 rounded-xl p-5'>
                <div className='font-extrabold text-base leading-snug mb-3'>THE SOLUTION</div>
                <div className='font-medium text-base leading-snug mb-3'>{Manifest.main[2]['main']}</div>
            </div>
        </div>
    </div>
    )
}

export default SectionContent
