import { useEffect } from "react";
import { useLocation } from "react-router-dom";

export default function ScrollToTop() {
  const { pathname } = useLocation();

  useEffect(() => {
    let el = document.querySelector('body');
    el.classList.add('fade-in');
    window.scrollTo(0, 0);
  }, [pathname]);

  return null;
}
